P_HTM[668]='*Kāyasaṅkhāro cittasaṅkhāro, cittasaṅkhāro kāyasaṅkhāro.*';
P_HTM[669]='*Vacīsaṅkhāro cittasaṅkhāro, cittasaṅkhāro vacīsaṅkhāro.*';
P_HTM[670]='*Paccanīkaṃ*';
