P_HTM[896]='*Anulomaṃ*';
P_HTM[897]='*Kusalā dhammā. Dhammā akusalā.*';
P_HTM[898]='*Kusalā dhammā. Dhammā abyākatā.*';
P_HTM[899]='*Akusalā dhammā. Dhammā kusalā.*';
P_HTM[900]='*Akusalā dhammā. Dhammā abyākatā.*';
P_HTM[901]='*Abyākatā dhammā. Dhammā kusalā.*';
