M_LOC[ 667] = "";
M_LOC[ 668] = "Kusāvatī was encircled by seven ramparts:";
M_LOC[ 669] = "one made of gold, one made of silver, one made of beryl, one made of crystal, one made of ruby, one made of emerald, and one made of all precious things.";
M_LOC[ 670] = "It had four gates,";
M_LOC[ 671] = "made of gold, silver, beryl, and crystal.";
M_LOC[ 672] = "At each gate there were seven pillars, three fathoms deep and four fathoms high,";
M_LOC[ 673] = "made of gold, silver, beryl, crystal, ruby, emerald, and all precious things.";
M_LOC[ 674] = "It was surrounded by seven rows of palm trees,";
M_LOC[ 675] = "made of gold, silver, beryl, crystal, ruby, emerald, and all precious things.";
M_LOC[ 676] = "The golden palms had trunks of gold, and leaves and fruits of silver.";
M_LOC[ 677] = "The silver palms had trunks of silver, and leaves and fruits of gold.";
M_LOC[ 678] = "The beryl palms had trunks of beryl, and leaves and fruits of crystal.";
M_LOC[ 679] = "The crystal palms had trunks of crystal, and leaves and fruits of beryl.";
M_LOC[ 680] = "The ruby palms had trunks of ruby, and leaves and fruits of emerald.";
M_LOC[ 681] = "The emerald palms had trunks of emerald, and leaves and fruits of ruby.";
M_LOC[ 682] = "The palms of all precious things had trunks of all precious things, and leaves and fruits of all precious things.";
M_LOC[ 683] = "When those rows of palm trees were blown by the wind they sounded graceful, tantalizing, sensuous, lovely, and intoxicating,";
M_LOC[ 684] = "like a quintet made up of skilled musicians who had practiced well and kept excellent rhythm.";
