M_LOC[1341] = "Long Discourses 22<br>The Longer Discourse on Mindfulness Meditation<br>So I have heard.";
M_LOC[1342] = "";
M_LOC[1343] = "";
M_LOC[1344] = "";
M_LOC[1345] = "What four?";
M_LOC[1346] = "It’s when a mendicant meditates by observing an aspect of the body—keen, aware, and mindful, rid of desire and aversion for the world.";
M_LOC[1347] = "They meditate observing an aspect of feelings—keen, aware, and mindful, rid of desire and aversion for the world.";
M_LOC[1348] = "They meditate observing an aspect of the mind—keen, aware, and mindful, rid of desire and aversion for the world.";
M_LOC[1349] = "They meditate observing an aspect of principles—keen, aware, and mindful, rid of desire and aversion for the world.";
M_LOC[1350] = "1. Observing the Body";
M_LOC[1351] = "1.1. Mindfulness of Breathing";
M_LOC[1352] = "And how does a mendicant meditate observing an aspect of the body?";
M_LOC[1353] = "";
M_LOC[1354] = "";
M_LOC[1355] = "";
M_LOC[1356] = "";
M_LOC[1357] = "";
