M_LOC[1373] = "";
M_LOC[1374] = "";
M_LOC[1375] = "";
M_LOC[1376] = "";
M_LOC[1377] = "";
M_LOC[1378] = "And how does a mendicant meditate observing an aspect of feelings?";
M_LOC[1379] = "It’s when a mendicant who feels a pleasant feeling knows: ‘I feel a pleasant feeling.’";
M_LOC[1380] = "When they feel a painful feeling, they know: ‘I feel a painful feeling.’";
M_LOC[1381] = "When they feel a neutral feeling, they know: ‘I feel a neutral feeling.’";
M_LOC[1382] = "When they feel a material pleasant feeling, they know: ‘I feel a material pleasant feeling.’";
M_LOC[1383] = "When they feel a spiritual pleasant feeling, they know: ‘I feel a spiritual pleasant feeling.’";
M_LOC[1384] = "When they feel a material painful feeling, they know: ‘I feel a material painful feeling.’";
M_LOC[1385] = "When they feel a spiritual painful feeling, they know: ‘I feel a spiritual painful feeling.’";
M_LOC[1386] = "When they feel a material neutral feeling, they know: ‘I feel a material neutral feeling.’";
M_LOC[1387] = "When they feel a spiritual neutral feeling, they know: ‘I feel a spiritual neutral feeling.’";
M_LOC[1388] = "And so they meditate observing an aspect of feelings internally, externally, and both internally and externally.";
M_LOC[1389] = "They meditate observing feelings as liable to originate, as liable to vanish, and as liable to both originate and vanish.";
M_LOC[1390] = "Or mindfulness is established that feelings exist, to the extent necessary for knowledge and mindfulness. They meditate independent, not grasping at anything in the world.";
M_LOC[1391] = "That’s how a mendicant meditates by observing an aspect of feelings.";
M_LOC[1392] = "3. Observing the Mind";
M_LOC[1393] = "And how does a mendicant meditate observing an aspect of the mind?";
M_LOC[1394] = "It’s when a mendicant knows mind with greed as ‘mind with greed,’ and mind without greed as ‘mind without greed.’ They know mind with hate as ‘mind with hate,’ and mind without hate as ‘mind without hate.’ They know mind with delusion as ‘mind with delusion,’ and mind without delusion as ‘mind without delusion.’ They know constricted mind as ‘constricted mind,’ and scattered mind as ‘scattered mind.’ They know expansive mind as ‘expansive mind,’ and unexpansive mind as ‘unexpansive mind.’ They know mind that is not supreme as ‘mind that is not supreme,’ and mind that is supreme as ‘mind that is supreme.’ They know mind immersed in meditation as ‘mind immersed in meditation,’ and mind not immersed in meditation as ‘mind not immersed in meditation.’ They know freed mind as ‘freed mind,’ and unfreed mind as ‘unfreed mind.’";
M_LOC[1395] = "";
M_LOC[1396] = "";
M_LOC[1397] = "";
M_LOC[1398] = "";
M_LOC[1399] = "";
