M_LOC[   1] = "Long Discourses 24";
M_LOC[   2] = "About Pāṭikaputta";
M_LOC[   3] = "1. On Sunakkhatta";
M_LOC[   4] = "So I have heard.";
M_LOC[   5] = "At one time the Buddha was staying in the land of the Mallas, near the Mallian town named Anupiya.";
M_LOC[   6] = "";
M_LOC[   7] = "";
M_LOC[   8] = "";
M_LOC[   9] = "";
