M_LOC[  13] = "";
M_LOC[  14] = "I declare this to you, Sunakkhatta, I announce this to you!";
M_LOC[  15] = "There will be those who say that Sunakkhatta was unable to lead the spiritual life under the ascetic Gotama. That’s why he rejected the training and returned to a lesser life.";
M_LOC[  16] = "That’s what they’ll say.’";
M_LOC[  17] = "Though I spoke to Sunakkhatta like this, he still left this teaching and training, like someone on the highway to hell.";
M_LOC[  18] = "2. On Korakkhattiya";
M_LOC[  19] = "Bhaggava, this one time I was staying in the land of the Thūlus where they have a town named Uttarakā.";
M_LOC[  20] = "";
M_LOC[  21] = "";
M_LOC[  22] = "";
